import React from "react";

const Header = (props) => {
	return (
		<h1>{props.name}</h1>
	)
}

const Part = (part) => {
	return (
		<p>{part.name} {part.exercises}</p>
	)
}

const Content = (parts) => {
	const items = []
	let total = 0
	console.log(parts)
	for (part of parts) {
		items.push(<Part name={part.name} exercises={part.exercises}/>)
		total += part.exercises
	}

	return (
		<>
			{items}
			<p>{total}</p>
		</>
	)
}

const App = () => {
	const courses = [
		{
			name: 'Half Stack application development',
			parts: [
				{
					name: 'Fundamentals of React',
					exercises: 10
				},
				{
					name: 'Using props to pass data',
					exercises: 7
				},
				{
					name: 'State of a component',
					exercises: 14
				}
			]
		}
	];

	return (
		<>
			{courses.map((val, i) => {
				return (
					<div>
						<Header name={val.name}/>
						<Content parts={val.parts}/>
					</div>
				)})
			}
		</>

  	)
}

export default App;
