import React, {useState} from "react"

const Button = ({text, clickHandler}) => {
	return (
		<button onClick={clickHandler}>
			{text}
		</button>
	)
}

const Header = ({tex}) => {
	return (
		<h1>{text}</h1>
	)
}

const Results = ({good, neutral, bad}) => {
	return (
		<ul>
			<li>good {good}</li>
			<li>netural {neutral}</li>
			<li>bad {bad}</li>
		</ul>
	)
}

const App = () => {
	const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  return (
		<div>
			<Header text="give feedback"/>
			<Button text="good" clickHandler={()=>setGood(good +1)}/>
			<Button text="neutral" clickHandler={()=>setNeutral(neutral+1)}/>
			<Button text="bad" clickHandler={()=>setBad(bad+1)}/>
			<Header text="statistics"/>
			<Results good={good} neutral={neutral} bad={bad}/>
		</div>
  )
}

export default App;
